mapper.select:


     and c.user_id = #{userId}
     and c.question like concat('%', #{question}, '%')
     and c.category_id = #{categoryId}
     and c.status = #{status}
     and c.rating = #{rating}
     and c.rating >= #{minRating}
     and c.rating <= #{maxRating}
     and c.question like concat('%', #{params.questionKeyword}, '%')
     and c.answer like concat('%', #{params.answerKeyword}, '%')

        and date_format(c.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


        and date_format(c.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


        and date_format(c.update_time,'%y%m%d') >= date_format(#{params.updateBeginTime},'%y%m%d')


        and date_format(c.update_time,'%y%m%d') <= date_format(#{params.updateEndTime},'%y%m%d')


order by c.create_time desc

mapper.update:
update ai_legal_consultation
user_id = #{userId}, question = #{question}, category_id = #{categoryId}, answer = #{answer}, confidence = #{confidence}, status = #{status}, attachment_path = #{attachmentPath}, rating = #{rating}, feedback = #{feedback}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where consultation_id = #{consultationId}
